import React, {Component} from "react";
import logo from './logo.svg';
import './App.css';


class App extends Component {
    constructor() {
        super();
        this.state = {
            monsters: [
                {
                    name: 'Frankenstein'
                },
                {
                    name: 'Frankenstein'
                },
                {
                    name: 'Frankenstein'
                },
            ]
        }
    }
    render () {
        return (
            <div className="App">
              <header className="App-header">
                <img src={logo} className="App-logo" alt="logo" />
                <p>
                    {this.state.string}
                </p>
                  <button onClick={()=> this.setState({string: "Bye Nastya"})}>Change text</button>
              </header>
            </div>
        );
    }
}

export default App;
